var fs = require('fs'), toc = require('markdown-toc');


if (!process.argv[2])
{
    console.log('generates table of contents for a markdown file');
    console.log('usage: node gentoc.js FILE.md');
}
else
{
    var markdown = fs.readFileSync(process.argv[2]).toString();
    var parsed = toc(markdown, { firsth1: false, bullets: '*' });
    console.log(parsed.content);
}
